    'Solder Alloy': ['Manufacturer', 'Grade', 'Form', 'Recycled Content', 'Perform Size', 'Solder Alloy'],
    'Shaped Metal': ['Manufacturer', 'Subtype', 'Product Name', 'Grade', 'Form', 'Metal/Alloy', 'Recycled Content', 'Standard and Standard Designating Body', 'Thickness'],
    'Bulk Metal': ['Manufacturer', 'Grade', 'Form', 'Metal/Alloy', 'Purity/Concentration', 'Recycled Content', 'Standard and Standard Designating Body'],
    'Plating': ['Manufacturer', 'Form', 'Manufacturer Performing Plating', 'Metal/Alloy', 'Purity/Concentration', 'Recycled Content'],
    'Chemical': ['Manufacturer', 'Chemical Name / Description', 'Purity/Concentration'],
    'Magnet': ['Manufacturer', 'Subtype', 'Grade', 'Recycled Content'],
    'Process': ['Manufacturer', 'Subtype', 'Product Name', 'Grade'],
    'Semiconductor': ['Manufacturer', 'Grade', 'Semiconductor Description', 'Semiconductor Type'],
    'Surface Treatment Process': ['Manufacturer', 'Subtype', 'Color', 'Composition of Coating / &quot;Target&quot; Material', 'Purity/Concentration', 'Substrate: Base Metal', 'Surface Treatment Type / Specification'],
    'Anodization': ['Manufacturer', 'Color', 'Anodization Details', 'Metal + &quot;Anodizing&quot;', 'Substrate: Base Metal']
}

@st.cache_data
def build_menu_config(df, headers):
    menu = {}
    for mat, cols in headers.items():
        sub = df[df[&quot;Material&quot;] == mat]
        menu[mat] = {
            c: sub[c].dropna().unique().tolist() if c in sub.columns else []
            for c in cols
        }
    return menu

menu_config = build_menu_config(df, menu_config_headers)

# -------------------------------------------------
# Layout
# -------------------------------------------------
form_col, preview_col = st.columns([1, 1])

# =================================================
# FORM
# =================================================
with form_col:
    # -------------------------------------------------
    # Get existing row if editing
    # -------------------------------------------------
    existing_row = None
    if st.session_state.edit_id:
        existing_row = st.session_state.materials_df[
            st.session_state.materials_df[&quot;ID&quot;] == st.session_state.edit_id
        ].squeeze()

    # -------------------------------------------------
    # Type selector (left label)
    # -------------------------------------------------
    label_col, input_col = st.columns([0.35, 0.65])

    with label_col:
        st.markdown(
            &quot;&lt;div style='text-align: right; padding-right: 8px; font-weight: 600;'&gt;Type&lt;/div&gt;&quot;,
            unsafe_allow_html=True
        )

    with input_col:
        category = st.selectbox(
            &quot;&quot;,
            [&quot;&quot;] + list(menu_config.keys()),
            index=0 if existing_row is None else list(menu_config.keys()).index(existing_row[&quot;Type&quot;]) + 1,
            label_visibility=&quot;collapsed&quot;
        )

    selected_values = {}

    # -------------------------------------------------
    # Dynamic fields
    # -------------------------------------------------
    if category:
        # Assign unique material_id for keys
        if st.session_state.edit_id:
            material_id = st.session_state.edit_id
        else:
            # Temporary ID for new material
            if &quot;new_material_id&quot; not in st.session_state:
                st.session_state.new_material_id = str(uuid.uuid4())
            material_id = st.session_state.new_material_id

        for label, options in menu_config[category].items():
            options_with_add = [&quot;&quot;, &quot;➕ Add new&quot;] + options
            existing_value = (
                existing_row[label] if existing_row is not None and label in existing_row else None
            )

            # Determine if existing value is a custom value
            is_custom = existing_value not in options and existing_value not in [&quot;&quot;, None]

            # Label + dropdown
            label_col, input_col = st.columns([0.35, 0.65])
            with label_col:
                st.markdown(
                    f&quot;&lt;div style='text-align: right; padding-right: 8px; font-weight: 600;'&gt;{label}&lt;/div&gt;&quot;,
                    unsafe_allow_html=True
                )

            # Selectbox index: either existing value, or &quot;Add new&quot; if custom
            if existing_value in options_with_add:
                default_index = options_with_add.index(existing_value)
            elif is_custom:
                default_index = options_with_add.index(&quot;➕ Add new&quot;)
            else:
                default_index = 0

            # Unique keys per material + field
            select_key = f&quot;select-{label}-{material_id}&quot;
            input_key = f&quot;input-{label}-{material_id}&quot;

            with input_col:
                choice = st.selectbox(
                    &quot;&quot;,
                    options_with_add,
                    index=default_index,
                    key=select_key,
                    label_visibility=&quot;collapsed&quot;
                )

            # Show &quot;Add new&quot; input if selected OR existing value is custom
            show_custom_input = choice == &quot;➕ Add new&quot;
            if show_custom_input:
                _, input_col_custom = st.columns([0.45, 0.55])
                with input_col_custom:
                    custom_value = st.text_input(
                        f&quot;↳ Enter new {label}&quot;,
                        value=existing_value if is_custom else &quot;&quot;,
                        key=input_key
                    )
                selected_values[label] = custom_value
            else:
                selected_values[label] = choice or None

        record = {&quot;Type&quot;: category, **selected_values}


        # -------------------------------------------------
        # Buttons row: Center Update/Create, Cancel to right
        # -------------------------------------------------
        # Three columns: left spacer, update button, cancel button
        col_left, col_update, col_cancel = st.columns([2, 1, 0.5])

        with col_update:
            btn_label = &quot;Update Material&quot; if st.session_state.edit_id else &quot;Create Material&quot;
            clicked = st.button(btn_label, key=&quot;create_update_material&quot;)

            if clicked:
                if not st.session_state.edit_id:
                    # CREATE
                    record[&quot;ID&quot;] = str(uuid.uuid4())
                    st.session_state.materials_df = pd.concat(
                        [st.session_state.materials_df, pd.DataFrame([record])],
                        ignore_index=True
                    )
                else:
                    # UPDATE
                    row_idx = st.session_state.materials_df.index[
                        st.session_state.materials_df[&quot;ID&quot;] == st.session_state.edit_id
                    ][0]
                    for k, v in record.items():
                        st.session_state.materials_df.loc[row_idx, k] = v
                    st.session_state.edit_id = None

                st.success(&quot;Material saved&quot;)
                st.rerun()

        # Show Cancel button only in edit mode
        if st.session_state.edit_id:
            with col_cancel:
                if st.button(&quot;Cancel&quot;, key=&quot;cancel_edit&quot;):
                    st.session_state.edit_id = None
                    st.rerun()

# =================================================
# PREVIEW / EDIT / DELETE
# =================================================
with preview_col:
    # st.subheader(&quot;Created Materials&quot;)

    content, spacer = st.columns([0.9, 0.05])
    with content:
        if st.session_state.materials_df.empty:
            st.info(&quot;No materials created yet.&quot;)
        else:
            delete_uid = None
            edit_uid = None

            for _, row in st.session_state.materials_df.iterrows():
