struct foo {
    static Int max() {
        return Max;
    }
    static std::string name() {
        return typeid(foo).name();  // &lt;===== buggy?
    }
};

int main() {
    // Most unsigned integer types work as expected.
    std::cout &lt;&lt; foo&lt;unsigned int&gt;::name()
        &lt;&lt; &quot;\nmax: &quot; &lt;&lt; foo&lt;unsigned int&gt;::max()
        &lt;&lt; &quot;\n\n&quot;;

    // Type `unsigned __int64` does not.
    std::cout &lt;&lt; foo&lt;unsigned long long&gt;::name()
        &lt;&lt; &quot;\nmax: &quot; &lt;&lt; foo&lt;unsigned long long&gt;::max()
        &lt;&lt; &quot;\n\n&quot;;
}
// end file: main.cpp
